function checkArrowSimilarProducts() {
  var e = document.querySelectorAll(
      ".install-video-carousel.swiper-button-prev"
    ),
    t = document.querySelectorAll(
      ".install-video-carousel .swiper-button-next"
    );
  e.forEach(function (e) {
    window.innerWidth < 1024
      ? (e.style.display = "none")
      : (e.style.display = "block");
  }),
    t.forEach(function (e) {
      window.innerWidth < 1024
        ? (e.style.display = "none")
        : (e.style.display = "block");
    });
}
let playVideoBtn = document.querySelectorAll(".play-video-btn"),
  installVideoIframe = document.getElementById("installVideoIframe"),
  installVideoUrl = "",
  dataUrl = "";
$("#installvideoModal").on("hidden.bs.modal", function (e) {
  installVideoIframe.src = "";
}),
  (installVideoIframe.src = " "),
  playVideoBtn.forEach(function (e) {
    e.addEventListener("click", function () {
      (installVideoUrl = this.parentElement.getAttribute("data-url")),
        (dataUrl = installVideoIframe.getAttribute("src")),
        (installVideoIframe.src = installVideoUrl),
        $("#installvideoModal").modal("show");
    });
  });
let swipableCardsSwiper = new Swiper(".swipable-cards .swiper-container", {
  centeredSlides: !1,
  slidesPerView: 3,
  spaceBetween: 30,
  loop: !1,
  allowTouchMove: !1,
  speed: 400,
  navigation: {
    nextEl: ".swipable-cards  .swiper-button-next",
    prevEl: ".swipable-cards  .swiper-button-prev",
  },
  pagination: { el: ".swipable-cards  .swiper-pagination", clickable: !0 },
  breakpoints: {
    480: {
      slidesPerView: 1.2,
      spaceBetween: 16,
      centeredSlides: !1,
      loop: !0,
      loopedSlides: 3,
      pagination: { el: ".swipable-cards .swiper-pagination", clickable: !0 },
      allowTouchMove: !1,
      navigation: !1,
    },
    991: {
      slidesPerView: 2.2,
      centeredSlides: !1,
      spaceBetween: 30,
      allowTouchMove: !1,
      pagination: { el: ".swipable-cards .swiper-pagination", clickable: !0 },
      navigation: !0,
    },
  },
  on: {
    init: function () {
      checkArrow();
    },
    resize: function () {
      checkArrow();
    },
  },
});
function checkArrow() {
  var e = document.querySelectorAll(".swipable-cards .swiper-button-prev"),
    t = document.querySelectorAll(".swipable-cards  .swiper-button-next");
  e.forEach(function (e) {
    window.innerWidth < 1024
      ? (e.style.display = "none")
      : (e.style.display = "block");
  }),
    t.forEach(function (e) {
      window.innerWidth < 1024
        ? (e.style.display = "none")
        : (e.style.display = "block");
    });
}
